rocks = {}
rocks[0] = ([(0, 0), (0, 1), (0, 2), (0, 3)], 1)
rocks[1] = ([(0, 1), (1, 1), (1, 2), (1, 0), (2, 1)], 3)
rocks[2] = ([(0, 0), (0, 1), (0, 2), (1, 2), (2, 2)], 3)
rocks[3] = ([(0, 0), (1, 0), (2, 0), (3, 0)], 4)
rocks[4] = ([(0, 0), (0, 1), (1, 0), (1, 1)], 2)

