cave = {}


def add_cave_level(ch="."):
    level = len(cave)
    cave[level] = {i: ch for i in range(0, 7)}


add_cave_level("-")
for i in range(100000):
    add_cave_level()


def display_cave(max_level):

    levels = list(cave.keys())[0: max_level+10]
    levels.reverse()
    for level in levels:
        str = ""

        for i in range(0, 7):
            str += cave[level][i]

        print(str)

    print()


def move_rock(cave, rock, pos, direction):

    # Replace its current location with .
    for pt in rock[0]:
        loc = (pos[0] + pt[0], pos[1] + pt[1])
        cave[loc[0]][loc[1]] = '.'

    can_move = True
    for pt in rock[0]:

        loc = (pos[0] + pt[0] + direction[0], pos[1] + pt[1] + direction[1])

        if loc[1] < 0 or loc[1] > 6 or cave[loc[0]][loc[1]] != '.':
            can_move = False

    # Place the rock in either its current or new position
    if can_move:
        direction = direction
    else:
        direction = (0, 0)
    for pt in rock[0]:
        loc = (pos[0] + pt[0] + direction[0], pos[1] + pt[1] + direction[1])

        cave[loc[0]][loc[1]] = "#"

    return can_move


curr_rock = 0
max_level = 0
num_rocks = 0
gas_idx = 0
tuple_to_level_dict = {}

check_for_cycle = True
num_rocks_to_drop = 1000000000000
while num_rocks < num_rocks_to_drop:

    rock = rocks[curr_rock]

    position = (max_level + 4, 2)
    move_rock(cave, rock, position, direction=(0, 0))

    # display_cave(max_level)
    #input("hit key")

    can_move_down = True
    while can_move_down:

        gas_dir = gas[gas_idx]
        direction = (0, 1) if gas_dir == ">" else (0, -1)
        can_move_gas = move_rock(cave, rock, position, direction)

        if can_move_gas:
            position = (position[0] + direction[0], position[1] + direction[1])

        direction = (-1, 0)
        can_move_down = move_rock(cave, rock, position, direction)

        if can_move_down:
            position = (position[0] + direction[0], position[1] + direction[1])

        # print(
        #    f"curr_rock = {curr_rock}; move {gas_dir} can_move_down = {can_move_down}")
        # display_cave(max_level)

        gas_idx += 1
        if gas_idx == len(gas):
            gas_idx = 0

    max_level = max(max_level, position[0] + rock[1] - 1)

    if check_for_cycle and curr_rock == 0 and max_level == position[0] + rock[1] - 1:

        tup = (curr_rock, gas_idx, position[1])

        if tup in tuple_to_level_dict:
            prev_max_level, prev_num_rocks = tuple_to_level_dict[tup]
            levels_per_cycle = max_level - prev_max_level

            num_rocks_per_cycle = (num_rocks - prev_num_rocks)

            num_additional_cycles = (num_rocks_to_drop -
                                     max_level) // num_rocks_per_cycle

            subtr = max_level
            print(
                f"Hey look!   We already encountered {tup} before.  The previous level was {prev_max_level} and now the level is {max_level}; levels_per_cycle = {levels_per_cycle}.  Current number of rocks is {num_rocks} and previously it was {prev_num_rocks}")

            # Update the number of rocks
            num_rocks += num_additional_cycles*num_rocks_per_cycle
            num_levels_skipped = num_additional_cycles*levels_per_cycle

            print(
                f"The number of rocks in a cycle is {num_rocks_per_cycle} and the # of additional cycles is {num_additional_cycles}.  Updating num_rocks = {num_rocks}; num_levels_skipped = {num_levels_skipped}")

            check_for_cycle = False

        else:
            tuple_to_level_dict[tup] = (max_level, num_rocks)

    curr_rock += 1
    if curr_rock == 5:
        curr_rock = 0

    num_rocks += 1

    # display_cave(max_level)
    #input("hit key")


